import React, { useState, useEffect } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend, BarChart, Bar, XAxis, YAxis } from 'recharts';
import Header from './Components/Header';
import ReportForm from './Components/ReportForm';
import Dashboard from './Pages/Dashboard';
import { AGRO_DATA_STORE } from './Interfaces/PlantReport';

function AppTemp() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [reports, setReports] = useState(AGRO_DATA_STORE);
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [weather, setWeather] = useState({ temp: 24, condition: 'G√ºne≈üli' });
  const [notes, setNotes] = useState("G√ºbreleme sevkiyatƒ± saat 14:00'te ba≈ülayacak.");

  // Grafik Verileri
  const chartData = [
    { name: 'Aktif Analiz', value: reports.filter(r => r.status === 'Analiz Ediliyor').length },
    { name: 'Tamamlanan', value: reports.filter(r => r.status === 'Kapatƒ±ldƒ±').length },
  ];
  const COLORS = ['#10b981', '#3b82f6'];

  const handleLogin = (e) => {
