import React, { useState } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import Header from './Components/Header';
import ReportForm from './Components/ReportForm';
import Dashboard from './Pages/Dashboard';
import { AGRO_DATA_STORE } from './Interfaces/PlantReport';

function AppTemp() {
  // Giriş kontrolü - false olduğu için önce login ekranı açılır
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [reports, setReports] = useState(AGRO_DATA_STORE);
  const [loginData, setLoginData] = useState({ username: '', password: '' });

  // Grafik Verisi: Mevcut raporlardaki durumların sayısal dağılımı
  const chartData = [
    { name: 'Analiz Ediliyor', value: reports.filter(r => r.status === 'Analiz Ediliyor').length },
    { name: 'Kapatıldı', value: reports.filter(r => r.status === 'Kapatıldı').length },
  ];
  const COLORS = ['#f59e0b', '#15803d']; // Turuncu (Analiz), Yeşil (Kapatıldı)

  const handleLogin = (e) => {
    e.preventDefault();
    // Kurumsal Giriş Bilgileri
    if (loginData.username === 'admin' && loginData.password === 'agro123') {
      setIsLoggedIn(true);
    } else {
      alert('Hatalı kullanıcı adı veya şifre! (İpucu: admin / agro123)');
    }
  };

  // 1. DURUM: KULLANICI GİRİŞ YAPMAMIŞSA (LOGIN EKRANI)
  if (!isLoggedIn) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-slate-100 px-4 font-sans">
        <div className="max-w-md w-full bg-white rounded-2xl shadow-2xl p-10 border border-slate-200">
          <div className="text-center mb-8">
            <h2 className="text-3xl font-extrabold text-green-800">AgroSmart</h2>
            <p className="text-slate-500 text-sm mt-2">Kurumsal Yönetim Paneli</p>
          </div>
          
          <form onSubmit={handleLogin} className="space-y-5">
            <div>
              <label className="block text-xs font-semibold text-slate-600 uppercase mb-1">Kullanıcı Adı</label>
              <input 
                type="text" 
                placeholder="admin" 
                className="w-full p-3 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-green-500 outline-none transition"
                onChange={(e) => setLoginData({...loginData, username: e.target.value})}
              />
            </div>
            <div>
              <label className="block text-xs font-semibold text-slate-600 uppercase mb-1">Şifre</label>
              <input 
                type="password" 
                placeholder="••••••••" 
                className="w-full p-3 bg-slate-50 border border-slate-200 rounded-xl focus:ring-2 focus:ring-green-500 outline-none transition"
                onChange={(e) => setLoginData({...loginData, password: e.target.value})}
              />
            </div>
            <button className="w-full bg-green-700 hover:bg-green-800 text-white font-bold py-3 rounded-xl shadow-lg transform active:scale-95 transition duration-200">
              Sisteme Giriş Yap
            </button>
          </form>
          
          <div className="mt-8 pt-6 border-t border-slate-100 text-center">
            <p className="text-[10px] text-slate-400 leading-relaxed uppercase tracking-widest">
              Yıldız Holding Bilgi Güvenliği Protokolü <br/> Altında Korunmaktadır
            </p>
          </div>
        </div>
      </div>
    );
  }

  // 2. DURUM: GİRİŞ BAŞARILIYSA (ANA PANEL)
  return (
    <div className="min-h-screen pb-20 px-6 bg-slate-50 font-sans">
      <Header />
      
      <div className="max-w-7xl mx-auto">
        {/* Üst Bölüm: Form ve Grafik Yan Yana */}
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8 mb-10">
          
          {/* Sol: Rapor Giriş Formu */}
          <div className="lg:col-span-2 bg-white p-8 rounded-2xl shadow-sm border border-slate-200">
            <h3 className="text-lg font-bold text-slate-800 mb-6 border-l-4 border-green-600 pl-3">Yeni Analiz Kaydı</h3>
            <ReportForm onAdd={(d) => setReports([{id: Date.now(), ...d, status: 'Analiz Ediliyor'}, ...reports])} />
          </div>
          
          {/* Sağ: Durum Dağılım Grafiği */}
          <div className="bg-white p-8 rounded-2xl shadow-sm border border-slate-200 flex flex-col items-center justify-center">
            <h3 className="text-sm font-bold text-slate-500 mb-6 uppercase tracking-wider">Güncel Durum Özeti</h3>
            <ResponsiveContainer width="100%" height={220}>
              <PieChart>
                <Pie 
                  data={chartData} 
                  innerRadius={65} 
                  outerRadius={85} 
                  paddingAngle={8} 
                  dataKey="value"
                  animationDuration={1000}
                >
                  {chartData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip cornerRadius={10} borderStyle={{ borderRadius: '10px' }} />
                <Legend iconType="circle" wrapperStyle={{ paddingTop: '20px' }} />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Alt Bölüm: Dashboard (Tablo) */}
        <div className="bg-white rounded-2xl shadow-sm border border-slate-200 overflow-hidden">
          <Dashboard 
            reports={reports} 
            onDelete={(id) => setReports(reports.filter(r => r.id !== id))}
            onUpdate={(id) => setReports(reports.map(r => r.id === id ? {...r, status: 'Kapatıldı'} : r))}
          />
        </div>
      </div>

      <footer className="max-w-7xl mx-auto mt-12 flex justify-between items-center text-slate-400 text-xs px-2">
        <p>© 2026 AgroSmart Proje Denetim Sistemi</p>
        <p className="font-semibold text-slate-500 italic">Geliştirici: Fatih AKYOL</p>
      </footer>
    </div>
  );
}

export default AppTemp;
