import React, { useState, useEffect } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import Header from './Components/Header';
import ReportForm from './Components/ReportForm';
import Dashboard from './Pages/Dashboard';
import { AGRO_DATA_STORE } from './Interfaces/PlantReport';

function AppTemp() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [reports, setReports] = useState(AGRO_DATA_STORE);
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => setIsLoading(false), 1000); // Y√ºklenme efekti sim√ºlasyonu
  }, []);

  const chartData = [
    { name: 'Analiz Ediliyor', value: reports.filter(r => r.status === 'Analiz Ediliyor').length },
    { name: 'Kapatƒ±ldƒ±', value: reports.filter(r => r.status === 'Kapatƒ±ldƒ±').length },
  ];
  const COLORS = ['#10b981', '#3b82f6'];

  const handleLogin = (e) => {
    e.preventDefault();
    if (loginData.username === 'admin' && loginData.password === 'agro123') {
      setIsLoggedIn(true);
    } else {
      alert('Hatalƒ± giri≈ü!');
    }
  };

  const filteredReports = reports.filter(r => 
    r.plantName.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (!isLoggedIn) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-green-900 via-green-800 to-slate-900 px-4 animate-in fade-in duration-1000">
        <div className="max-w-md w-full bg-white/10 backdrop-blur-md rounded-3xl shadow-2xl p-10 border border-white/20 transform transition hover:scale-105 duration-500">
          <div className="text-center mb-8">
            <div className="w-20 h-20 bg-green-500 rounded-full mx-auto mb-4 flex items-center justify-center shadow-lg shadow-green-500/50 animate-pulse">
              <span className="text-3xl text-white font-bold">A</span>
            </div>
            <h2 className="text-3xl font-black text-white tracking-tight">AgroSmart</h2>
            <p className="text-green-200/70 text-sm mt-2">Yƒ±ldƒ±z Holding G√ºvenli Eri≈üim</p>
          </div>
          <form onSubmit={handleLogin} className="space-y-6">
            <input 
              type="text" placeholder="Kullanƒ±cƒ± Adƒ±" 
              className="w-full p-4 bg-white/5 border border-white/10 rounded-2xl text-white placeholder-green-200/50 outline-none focus:ring-2 focus:ring-green-400 transition"
              onChange={(e) => setLoginData({...loginData, username: e.target.value})}
            />
            <input 
              type="password" placeholder="≈ûifre" 
              className="w-full p-4 bg-white/5 border border-white/10 rounded-2xl text-white placeholder-green-200/50 outline-none focus:ring-2 focus:ring-green-400 transition"
              onChange={(e) => setLoginData({...loginData, password: e.target.value})}
            />
            <button className="w-full bg-green-500 hover:bg-green-400 text-white font-black py-4 rounded-2xl shadow-xl shadow-green-500/20 transition-all active:scale-95">
              Sƒ∞STEME Gƒ∞Rƒ∞≈û YAP
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen pb-20 px-6 bg-[#f8fafc] font-sans overflow-x-hidden">
      <Header />
      
      <main className="max-w-7xl mx-auto mt-8 animate-in slide-in-from-bottom-10 duration-700">
        
        {/* 1. YENƒ∞ √ñZELLƒ∞K: √ñZET KARTLARI */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
          {[
            {label: 'Toplam Analiz', val: reports.length, color: 'text-blue-600', bg: 'bg-blue-50'},
            {label: 'Bekleyen', val: chartData[0].value, color: 'text-amber-600', bg: 'bg-amber-50'},
            {label: 'Tamamlanan', val: chartData[1].value, color: 'text-green-600', bg: 'bg-green-50'}
          ].map((stat, i) => (
            <div key={i} className={`${stat.bg} p-6 rounded-3xl border border-white shadow-sm hover:shadow-md transition`}>
              <p className="text-xs font-bold uppercase tracking-wider text-slate-500">{stat.label}</p>
              <h4 className={`text-3xl font-black ${stat.color} mt-2`}>{stat.val}</h4>
            </div>
          ))}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          <div className="lg:col-span-2 space-y-8">
            <div className="bg-white p-8 rounded-[2rem] shadow-xl shadow-slate-200/50 border border-slate-100 relative overflow-hidden group">
               <div className="absolute top-0 left-0 w-2 h-full bg-green-500 group-hover:w-3 transition-all"></div>
               <h3 className="text-xl font-bold text-slate-800 mb-6 flex items-center">
                 <span className="mr-2 italic text-green-600">#</span> Yeni Analiz Giri≈üi
               </h3>
               <ReportForm onAdd={(d) => setReports([{id: Date.now(), ...d, status: 'Analiz Ediliyor'}, ...reports])} />
            </div>

            {/* 2. YENƒ∞ √ñZELLƒ∞K: ARAMA Fƒ∞LTRESƒ∞ */}
            <div className="bg-white p-4 rounded-2xl shadow-sm border border-slate-100 flex items-center">
              <span className="ml-4 mr-2">üîç</span>
              <input 
                type="text" 
                placeholder="Bitki adƒ±na g√∂re analizlerde ara..."
                className="w-full bg-transparent outline-none text-slate-600 text-sm"
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
          
          <div className="bg-white p-8 rounded-[2rem] shadow-xl shadow-slate-200/50 border border-slate-100 flex flex-col items-center justify-center">
            <h3 className="text-xs font-black text-slate-400 mb-8 uppercase tracking-[0.2em]">B√∂lgesel Veri Daƒüƒ±lƒ±mƒ±</h3>
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie data={chartData} innerRadius={70} outerRadius={90} paddingAngle={10} dataKey="value" stroke="none">
                  {chartData.map((entry, index) => <Cell key={`cell-${index}`} fill={COLORS[index]} />)}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
            <div className="mt-4 flex gap-4 text-[10px] font-bold uppercase">
               <div className="flex items-center"><span className="w-3 h-3 bg-emerald-500 rounded-full mr-2"></span> Aktif</div>
               <div className="flex items-center"><span className="w-3 h-3 bg-blue-500 rounded-full mr-2"></span> Kapalƒ±</div>
            </div>
          </div>
        </div>

        <div className="mt-10 bg-white rounded-[2rem] shadow-xl shadow-slate-200/50 border border-slate-100 overflow-hidden transition-all">
          <div className="p-6 bg-slate-50 border-b border-slate-100 flex justify-between items-center">
             <h3 className="font-bold text-slate-700">G√ºncel Analiz Kuyruƒüu</h3>
             <button onClick={() => window.print()} className="text-xs bg-white px-4 py-2 rounded-lg border shadow-sm hover:bg-slate-50 transition">üñ®Ô∏è Rapor Al</button>
          </div>
          <Dashboard 
            reports={filteredReports} 
            onDelete={(id) => setReports(reports.filter(r => r.id !== id))}
            onUpdate={(id) => setReports(reports.map(r => r.id === id ? {...r, status: 'Kapatƒ±ldƒ±'} : r))}
          />
        </div>
      </main>

      <footer className="mt-16 text-center text-slate-400 text-[10px] uppercase tracking-widest">
        AgroSmart | Yƒ±ldƒ±z Holding Dijital D√∂n√º≈ü√ºm Projesi - 2026
      </footer>
    </div>
  );
}

export default AppTemp;
