import React, { useState } from 'react';
import Header from './Components/Header';
import ReportForm from './Components/ReportForm';
import Dashboard from './Pages/Dashboard';
import { AGRO_DATA_STORE } from './Interfaces/PlantReport';

function App() {
  const [reports, setReports] = useState(AGRO_DATA_STORE);

  const handleAdd = (newData) => {
    const newEntry = { id: Math.floor(Math.random() * 900) + 100, ...newData, status: 'Analiz Ediliyor' };
    setReports([newEntry, ...reports]);
  };

  const handleDelete = (id) => {
    setReports(reports.filter(r => r.id !== id));
  };

  const handleUpdate = (id) => {
    setReports(reports.map(r => r.id === id ? { ...r, status: 'Kapatıldı' } : r));
  };

  return (
    <div className="min-h-screen pb-20 px-6">
      <Header />
      <ReportForm onAdd={handleAdd} />
      <Dashboard reports={reports} onDelete={handleDelete} onUpdate={handleUpdate} />
      <footer className="max-w-6xl mx-auto mt-16 text-slate-400 text-xs flex justify-between border-t border-slate-100 pt-6">
        <span>© 2026 AgroSmart Teknoloji A.Ş. Tüm hakları saklıdır.</span>
        <span className="font-medium">Geliştiren: [Senin Adın Soyadın]</span>
      </footer>
    </div>
  );
}

export default App;
