import React, { useState, useEffect } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend, BarChart, Bar, XAxis, YAxis, CartesianGrid } from 'recharts';
import Header from './Components/Header';
import ReportForm from './Components/ReportForm';
import Dashboard from './Pages/Dashboard';
import { AGRO_DATA_STORE } from './Interfaces/PlantReport';

function AppTemp() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [reports, setReports] = useState(AGRO_DATA_STORE);
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [stock, setStock] = useState(74); // √ñzellik 1: Stok Durumu
  const [darkMode, setDarkMode] = useState(false);

  const chartData = [
    { name: 'Aktif', value: reports.filter(r => r.status === 'Analiz Ediliyor').length },
    { name: 'Kapalƒ±', value: reports.filter(r => r.status === 'Kapatƒ±ldƒ±').length },
  ];
  const COLORS = ['#22c55e', '#3b82f6'];

  const handleLogin = (e) => {
    e.preventDefault();
    if (loginData.username === 'admin' && loginData.password === 'agro123') {
      setIsLoggedIn(true);
      const msg = new SpeechSynthesisUtterance("Agro Smart Sistemine Giri≈ü Yapƒ±ldƒ±.");
      window.speechSynthesis.speak(msg);
    } else {
      alert('Hatalƒ± Giri≈ü!');
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-[#050505] overflow-hidden relative font-sans">
        {/* Tasarƒ±m 1: Hareketli Par√ßacƒ±klar Arka Plan */}
        <div className="absolute inset-0 opacity-20 pointer-events-none">
          <div className="absolute top-10 left-10 w-64 h-64 bg-green-500 rounded-full mix-blend-screen blur-[100px] animate-pulse"></div>
          <div className="absolute bottom-10 right-10 w-64 h-64 bg-blue-500 rounded-full mix-blend-screen blur-[100px] animate-pulse delay-700"></div>
        </div>
        
        <div className="max-w-md w-full bg-white/5 backdrop-blur-xl rounded-[3rem] shadow-[0_0_50px_rgba(0,0,0,0.5)] p-12 border border-white/10 z-10 transition-all hover:border-green-500/30">
